import React from 'react';

import {Link} from 'react-router-dom';

import Hero from '../components/Hero';

import singleProductBackground from '../images/singleProductBcg.jpeg';

import {ProductConsumer} from '../context';

const SingleProductPage = () => {

  return (

    <>

      <Hero img = {singleProductBackground} title = 'single product' />

      <ProductConsumer>

        {value => {

          const { singleProduct, loading } = value;

          if (loading) {return <h1> Cargando... </h1>;}

          const {company, description, id, price, title, image} = singleProduct;

          return (

            <section className = 'py-5'>

              <div className = 'container'>

                <div className = 'row'>

                  <div className = 'col-10 mx-auto col-sm-8 col-md-6 my-3'>

                    <img src = {`../${image}`} alt = 'single product' className = 'img-fluid'/>

                  </div>

                  <div className = 'col-10 mx-auto col-sm-8 col-md-6 my-3'>

                    <h5 className = 'text-title mb-4'> {title} </h5>

                    <h5 className = 'text-capitalize text-muted mb-4'> {company} </h5>

                    <h5 className = 'text-main text-capitalize mb-4'> {(price)} </h5>

                    <p className = 'text-capitalize text-title mt-3'> {description} </p>

                    <Link to = '/produtos' className = 'main-link' style = {{ margin: '0.75rem' }}> Voltar aos produtos </Link>

                  </div>

                </div>

              </div>

            </section>

          );

        }}

      </ProductConsumer>

    </>

  );

};

export default SingleProductPage;
