import React from 'react';
import { formatPrice } from '../../context/formatPrice';
import { FaTrash, FaPlusCircle, FaMinusCircle } from 'react-icons/fa';
export default function CartItem({
  cartItem,
  increment,
  decrement,
  removeItem
}) {
  const { id, title, price, count, total, image } = cartItem;

  return (
    <div className='row mt-5 mt-lg-0 text-capitalize text-center align-items-center'>
      {/* image */}
      <div className='col-10 mx-auto col-lg-2 pb-2'>
        <img src={image} width='60' className='img-fluid' alt='product' />
      </div>
      {/*end of image */}
      {/* title */}
      <div className='col-10 mx-auto col-lg-2 pb-2'>
        <span className='d-lg-none'>produto : </span>
        {title}
      </div>
      {/*end of title */}
      {/* price */}
      <div className='col-10 mx-auto col-lg-2 pb-2'>
        <span className='d-lg-none'>pre√ßo : </span>
        {formatPrice(price)}
      </div>
      {/*end of price */}
      {/* count controls */}

      <div className='col-10 mx-auto col-lg-2 my-2 my-lg-0'>
        <div className='d-flex justify-content-center'>
          <div>
            <FaMinusCircle
              onClick={() => decrement(id)}
              className='cart-icon text-primary'
            />
            <span className='text-title text-muted mx-3'>{count}</span>
            <FaPlusCircle
              onClick={() => increment(id)}
              className='cart-icon text-primary'
            />
          </div>
        </div>
      </div>

      {/* end count controls */}
      {/* remove item */}
      <div className='col-10 mx-auto col-lg-2 '>
        <FaTrash
          className='text-danger cart-icon'
          onClick={() => removeItem(id)}
        />
      </div>
      {/*end of remove */}
      {/* item total */}
      <div className='col-10 mx-auto col-lg-2'>
        <strong className='text-muted'>{formatPrice(total)}</strong>
      </div>
      {/* end item total */}
    </div>
  );
}
